import React, {Component} from 'react'
import {Grid, GridRow, GridColumn, Radio, Message, Button, CardContent, Container, Icon, Progress} from 'semantic-ui-react'
import axios from 'axios'
import _ from 'lodash'
import { Redirect } from 'react-router-dom';

import Header from './Header'


class Perguntas extends Component{
    constructor(props){
        super(props)
        this.state = {
            perguntas: {},
            estaCarregando: false,
            perguntaAtual: 0,
            totalPerguntas: 0,
            pontos: 0,
            resultado: [],
            terminei: false
        } 

        this.proximaPergunta = this.proximaPergunta.bind(this)
        this.onRadioChange = this.onRadioChange.bind(this)


    }
    componentDidMount(){
        this.carregandoPerguntas(this.props.match.params.nome)
    }

    carregandoPerguntas(cat){
        console.log('Categoria da pergunta: ', cat)
        this.setState({
            estaCarregando: true,
            perguntas: {}
        })

        const url = `https://projeto-react-653ae.firebaseio.com/categorias.json?orderBy="nome"&equalTo="${cat}"`
        axios
            .get(url)
            .then(dados => {
                const chave = Object.keys(dados.data)[0]
                const listaPerguntas = dados.data[chave]
                console.log('Chaveeee', chave)
                console.log('Lsita de perguntas', listaPerguntas)
                console.log('Quantidade de perguntas: ', _.size(listaPerguntas) - 1)
                this.setState({
                    estaCarregando: false,
                    perguntas: listaPerguntas,
                    totalPerguntas: (_.size(listaPerguntas)-1)
                })
            })
            .catch(err => {
                console.log('Algum problema ocorreu')
            })
    }

    proximaPergunta(){
        const {perguntaAtual, totalPerguntas} = this.state
        console.log(perguntaAtual, totalPerguntas)
        if(perguntaAtual < totalPerguntas -1){
            this.setState({perguntaAtual: this.state.perguntaAtual +1})

        }else{
            console.log('Terminou as perguntas, mostre as respostas')
            console.log('PONTUAÇÃO:', this.state.pontos)
            this.setState({
                terminei: true
            })

        }

    }
    onRadioChange = (e, {resposta, name}) => {
        console.log('Chegou no OnRadioChange: ', resposta)
        console.log('Alternativas ', this.state.perguntas.perguntas[name].alternativas)
        //console.log('A alternativa correta é: ', _.filter(this.state.perguntas.perguntas[name].alternativas, {'correta': true}))
        /*this.setState({resposta})

        const respostaJogador = resposta
        const respostaCorreta = _.filter(this.state.perguntas.perguntas[name].alternativas, {'correta': true})[0].resposta
        const acertou = (respostaJogador === respostaCorreta)

        console.log(`a resposta do jogador: ${resposta}`)
        console.log(`pergunta atual: ${this.state.perguntas.perguntas[name].titulo}`)
        console.log(`a alternativa correta: ${respostaCorreta}`)
        console.log(`acertou? ${acertou}`)
        const res = {
            pergunta: this.state.perguntas.perguntas[name].titulo,
            resposta,
            acertou
        }
        this.setState({resultado: [...this.state.resultado, res]})
        if (acertou) {
            this.setState({
                pontos: this.state.pontos + 1
            })
        }*/
    }

    renderPergunta(pergunta, id){
        return (
            <span >
                <h3>PERGUNTA:{pergunta.titulo}</h3>
                {console.log('aquiiii', pergunta)}
                <Grid columns={2}>
                <GridRow>              

                    <GridColumn>
                        <Message color='blue'>
                            {JSON.stringify(pergunta.alternativas[1].correta)}
                            <p>{pergunta.alternativas[1].resposta}</p>                                
                            <Radio toggle
                                name={id}
                                resposta={pergunta.alternativas[1].resposta}
                                onChange={this.onRadioChange}                            

                            />                            
                        </Message>                        

                    </GridColumn>
                    <GridColumn>
                        <Message color='pink'>
                            <p>{pergunta.alternativas[2].resposta}</p>                                
                            <Radio toggle
                                name={id}
                                resposta={pergunta.alternativas[2].resposta}
                                onChange={this.onRadioChange}                            

                            />                                                     
                        </Message>  
                    </GridColumn>
                </GridRow>

                <GridRow>
                    <GridColumn>
                        <Message color='orange'>
                            <p>{pergunta.alternativas[3].resposta}</p>                                
                            <Radio toggle
                                name={id}
                                resposta={pergunta.alternativas[3].resposta}
                                onChange={this.onRadioChange}                            

                            />                                                        
                        </Message>  
                    </GridColumn>
                    <GridColumn>
                        <Message color='green'>
                            <p>{pergunta.alternativas[4].resposta}</p>                                
                            <Radio toggle
                                name={id}
                                resposta={pergunta.alternativas[4].resposta}
                                onChange={this.onRadioChange}                            

                            />                                                     
                        </Message>  
                    </GridColumn>                        
                </GridRow>
            </Grid>
        </span>
        )
    }


    render(){

        let item = []
        if(this.state.estaCarregando){
            return <p>Carregando...</p>
        }

        return(
            <div>
                <Header /> 

                <h2><Icon name={this.state.perguntas.icone}/> {this.props.match.params.nome}</h2>
                <p>Mostre que você conhece tudo sobre esse assunto</p>

                {

                    this.state.perguntas.perguntas && Object.keys(this.state.perguntas.perguntas)
                    .map(key =>{
                        item.push(key)
                    })
                }
                {
                    this.state.perguntas.perguntas && this.renderPergunta(this.state.perguntas.perguntas[item[this.state.perguntaAtual]])


                }

                <Progress value={this.state.perguntaAtual + 1} total= {item.length} progress='ratio'/>
                <Container>
                    <CardContent>
                        <Button basic color='blue' onClick={this.proximaPergunta}>Próximo</Button>
                    </CardContent>
                </Container>   

            </div>    
        )
    }
    }

export default Perguntas
